mapper.update:
UPDATE cms_article
column_id=#{columnId}, title=#{title}, title_img=#{titleImg}, author=#{author}, content=#{content}, summary=#{summary}, href=#{href}, key_word=#{keyWord}, publish_time=#{publishTime}, is_top=#{isTop}, is_recommend=#{isRecommend}, sort=#{sort}, updater_id=#{updaterId}, update_time=#{updateTime},
WHERE id = #{id}

mapper.select:
SELECT id, title, title_img, summary, href, year(publish_time) _year, DATE_FORMAT(publish_time, '%m-%d') _date
FROM cms_article WHERE  status <> 0 
AND column_id = #{columnId}
ORDER BY is_top desc, sort asc, publish_time desc limit #{top}

SELECT a.id, a.title, a.title_img, a.summary, a.href, year(a.publish_time) _year, DATE_FORMAT(a.publish_time, '%m-%d') _date
FROM cms_article as a LEFT JOIN cms_column as c ON a.column_id = c.id WHERE  a.status <> 0 
AND c.parent_id = #{columnId}
ORDER BY a.is_top desc, a.sort asc, a.publish_time desc limit #{top}
